import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import firebase from './Firebase';
import { useState, useEffect } from 'react';
import { doc, getDoc } from "firebase/firestore";


class Image{
  constructor (url){
    this.url = url
  }
}

const root = ReactDOM.createRoot(document.getElementById('root'));
//Roots
const img1 = ReactDOM.createRoot(document.getElementById('img_1'))
const img2 = ReactDOM.createRoot(document.getElementById('img_2'))
const rating1 = ReactDOM.createRoot(document.getElementById('rating1'))
const rating2 = ReactDOM.createRoot(document.getElementById('rating2'))
// Image Buttons
const imgbtn1 = document.getElementById('img_1')
const imgbtn2 = document.getElementById('img_2')


// Collection reference
const ref = firebase.firestore().collection("images")


function display() {



  let id_1 = Math.floor(Math.random() * 2);
  let id_2 = Math.floor(Math.random() * 2);


  let image_1 = ref.doc(String(id_1))
  let image_2 = ref.doc(String(id_2))


  image_1.get().then((doc) => {
    if (doc.exists) {
        console.log("Image url:", doc.data().url);

        let url = doc.data().url
        let rating = doc.data().rating

        img1.render(<img className="image" src={url}></img>)
        rating1.render(rating)


    } else {
        // doc.data() will be undefinez in this case
        console.log("No such document!");
    }
  }).catch((error) => {
    console.log("Error getting document:", error);
  });

  image_2.get().then((doc) => {
    if (doc.exists) {
        console.log("Image url:", doc.data().url);

        let url = doc.data().url
        let rating = doc.data().rating

        img2.render(<img className="image" src={url}></img>)
        rating2.render(rating)


    } else {
        // doc.data() will be undefinez in this case
        console.log("No such document!");
    }
  }).catch((error) => {
    console.log("Error getting document:", error);
  });

}

display()

imgbtn1.onclick = function(){
  display
}


// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
