import './App.css';
import { useState,useEffect } from 'react';
import firebase from './Firebase';


function App(){

    const [data, setdata] = useState([])
    const [loader, setloader] = useState(true)

    firebase.firestore().collection("images").orderBy("rating", "desc").limit(5).onSnapshot((snapshot) => {
        const item = []
    snapshot.forEach((doc) =>{
        item.push(doc.data())

    })
    setdata(item)
    setloader(false)



    });


    return (
        <div className='App'>
            <h1>leaderboard</h1>
            {loader === false && (data.map((data) => (

                <div>{data.rating}
                <img src={data.url}></img>
                </div>
            )))}
        </div>
    )


}

export default App
